import React from "react";
import ReactDOM from "react-dom";
//import "./index.css";

let curr = new Date()
curr=curr.getHours();
let greeting=" ";
if(curr>=1 && curr<12){
  greeting="Good Morning";
}
else if(curr>=12 && curr<19){
  greeting="Good Afternoon";
}
else if(curr>=19){
  greeting="Good Night";
}

ReactDOM.render(
 <div className="box"><h1>Hello sir {greeting} </h1></div> ,

  document.getElementById("root")
);
